import '@vaadin/vaadin-checkbox/src/vaadin-checkbox.js';
import '@whcg/whcg-lit-checkbox/theme/whcg-lit-checkbox-styles.js';
import { html, PolymerElement } from '@polymer/polymer/polymer-element.js';

class WhcgLitCheckbox extends PolymerElement {
    static get template() {
        return html `
        <style>
             vaadin-checkbox {
                --whcg-checkbox-font-size: var(--whcg-checkbox-checkbox-font-size);
                --whcg-label-font-family: var(--whcg-checkbox-label-font-family);
                --whcg-label-font-weight: var(--whcg-checkbox-label-font-weight);
                --whcg-checkbox-border-radius: var(--whcg-checkbox-checkbox-border-radius);
                --whcg-checkbox-background-color: var(--whcg-checkbox-checkbox-background-color);
                --whcg-checkbox-host-checked-background-color: var(--whcg-checkbox-checkbox-host-checked-background-color);
                --whcg-checkbox-host-not-disabled-hover-background-color: var(--whcg-checkbox-checkbox-host-not-disabled-hover-background-color);
                --whcg-checkbox-host-disabled-background-color: var(--whcg-checkbox-checkbox-host-disabled-background-color);
                --whcg-checkbox-after-checkmark-color: var(--whcg-checkbox-checkbox-after-checkmark-color);
                
                --whcg-label-font-size: var(--whcg-checkbox-label-font-size);
                --whcg-label-host-disabled-color: var(--whcg-checkbox-label-host-disabled-color);
                --whcg-label-color: var(--whcg-checkbox-label-color);
             }
            

        </style>

        <vaadin-checkbox>checkbox</vaadin-checkbox>
        <vaadin-checkbox disabled checked>checkbox</vaadin-checkbox>
    `
    };   
}

window.customElements.define('whcg-lit-checkbox', WhcgLitCheckbox);

export { WhcgLitCheckbox };




