const $_documentContainer = document.createElement('template');

$_documentContainer.innerHTML = `<dom-module id="whcg-lit-checkbox-styles" theme-for="vaadin-checkbox">

  <template>
    <style>
      
      :host([disabled]) {
        pointer-events: none;
      }

      [part="label"] {
        color: var(--whcg-label-color);
        font-size: var(--whcg-label-font-size);
        font-family: var(--whcg-label-font-family);
        font-weight: var(--whcg-label-font-weight);
      }

      [part="label"]:not(:empty) {
        padding: 0.1875em 0.875em 0.1875em 0.375em;
      }

      :host([disabled]) [part="label"] {
       
        color: var(--whcg-label-host-disabled-color);
      }

      [part="checkbox"] {
        font-size: var(--whcg-checkbox-font-size);
        width: calc(1em + 2px);
        height: calc(1em + 2px);
        position: relative;
        border-radius: var(--whcg-checkbox-border-radius);
        background-color: var(--whcg-checkbox-background-color);
        pointer-events: none;
        line-height: 1.2;
      }

      :host([checked]) [part="checkbox"] {
        background-color: var(--whcg-checkbox-checkbox-host-checked-background-color);
      }

      :host(:not([disabled]):hover) [part="checkbox"] {
        background-color: var(--whcg-checkbox-host-not-disabled-hover-background-color);
      }

      :host([disabled]) [part="checkbox"] {
        background-color: var(--whcg-checkbox-host-disabled-background-color);
      }

      /* Needed to align the checkbox nicely on the baseline */
      [part="checkbox"]::before {
        content: "\\2003";
      }

      /* Checkmark */
      [part="checkbox"]::after {
        content: "";
        display: inline-block;
        width: 0;
        height: 0;
        border-style: solid;
        border-color: var(--whcg-checkbox-after-checkmark-color);
        border-width: 0.1875em 0 0 0.1875em;
        box-sizing: border-box;
        transform-origin: 0 0;
        position: absolute;
        top: 0.8125em;
        left: 0.5em;
        transform: scale(0.55) rotate(-135deg);
        opacity: 0;
        width: 0.625em;
        height: 1.0625em;
      }

      :host([checked]) [part="checkbox"]::after {
        opacity: 1;
      }
      
      
      
      
    </style>
  </template>
</dom-module>`;
      
      document.head.appendChild($_documentContainer.content);
      
