const $_documentContainer = document.createElement('template');

$_documentContainer.innerHTML = `<dom-module id="whcg-lit-radio-button" theme-for="vaadin-radio-button">
  <template>
    <style>
      :host([disabled]) {
        pointer-events: none;
      }
      [part="label"] {
        color: var(--whcg-label-color);
        font-size: var(--whcg-label-font-size);
        font-family: var(--whcg-label-font-family);
        font-weight: var(--whcg-label-font-weight);
      }
      
      [part="label"]:not(:empty) {
        padding: 0.1875em 0.875em 0.1875em 0.375em;
      }

      :host([disabled]) [part="label"] {
        color: var(--whcg-label-host-disabled-color);
      }

      [part="radio"] {
        font-size: var(--whcg-radio-font-size);
        width: calc(1em + 2px);
        height: calc(1em + 2px);
        position: relative;
        border-radius: 50%;
        background-color: var(--whcg-radio-background-color);
        pointer-events: none;
        line-height: 1.2;
      }

      :host([checked]) [part="radio"] {
        background-color: var(--whcg-radio-host-checked-background-color);
      }

      :host(:not([indeterminate]):not([disabled]):hover) [part="radio"] {
        background-color: var(--whcg-radio-host-not-disabled-hover-background-color);
      }

      :host(:not([checked]):not([indeterminate]):not([disabled]):hover) [part="radio"] {
        background-color: var(--whcg-radio-host-not-disabled-not-checked-hover-background-color);
      }

      :host([disabled]) [part="radio"] {
        background-color: var(--whcg-radio-host-disabled-background-color);
      }

      /* Needed to align the radio-button nicely on the baseline */
      [part="radio"]::before {
        content: "\\2003";
      }

      /* Used for the dot */
      [part="radio"]::after {
        content: "";
        width: 0;
        height: 0;
        border-style: solid;
        border-color: var(--whcg-radio-after-checkmark-color);
        border-width: 3px;
        border-radius: 50%;
        position: absolute;
        opacity: 0;
        top: 50%;
        left: 50%;
      }

      :host([checked]) [part="radio"]::after {
        opacity: 1;
      }

      
    </style>
  </template>
</dom-module>`;

document.head.appendChild($_documentContainer.content);
